import React, { useEffect, useState }  from 'react';
import axios from 'axios';
import { Link, navigate } from '@reach/router';
import DeleteButton from './DeleteButton';

// eslint-disable-next-line
export default (props) => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8000/api/products')
            .then(res => setProducts(res.data))
            .catch(err => console.log(err));
    }, [])

    const removeFromDom = productId => {
        setProducts(products.filter(prod => prod._id !== productId));
        console.log(productId);
    }

    // why removeFromDom is not used in Update or Detail??????
    // deleting works without removeFromDom
    // in ProductList.js
    //successCallback={()=>removeFromDom(value._id)}
    // in Update.js and Detail.js
    //successCallback={() => navigate("/")} 


    return (
        <div className="displayAll">
            {/* make sure products are in ARRAY or check the data type and device how to iterate */}

            <h1>All Products:</h1>
            {props.products.map((value, idx)=>{
                return <h4 className="productTitle" key={idx}> 
                    {/* <Link to="/products/:id"> {product.title} </Link> */}
                    <Link to={`/${value._id}`}> {value.title} </Link>

                    <Link to={`/${value._id}/edit`}>
                     <span>Edit</span>
                    </Link> 

                        {/* passing down the function as a prop instead of invoking the function */}
                    <DeleteButton productId={value._id} successCallback={removeFromDom}/>

                    </h4>
            })}

        </div>
    )
}

