import React, { useEffect, useState } from 'react'
import axios from 'axios';

// eslint-disable-next-line
export default props => {
    const { id } = props;
    const [title, setTitle] = useState(""); 
    const [price, setPrice] = useState("");
    const [description, setDescription] = useState("");

    useEffect(() => {
        axios.get('http://localhost:8000/api/products/' + id )
            .then(res => {
                setTitle(res.data.title);
                setPrice(res.data.price);
                setDescription(res.data.description);
            })
    }, [])
    const updateProduct = e => {
        e.preventDefault();
        axios.put('http://localhost:8000/api/products/' + id, {
            title,
            price,
            description
        })
            .then(res => console.log(res))
            .catch(err=>console.log(err));
    }
    return (
        // <div>
        //     <h1>Update a Person</h1>
        //     <form onSubmit={updatePerson}>
        //         <p>
        //             <label>First Name</label><br />
        //             <input type="text" 
        //             name="firstName" 
        //             value={firstName} 
        //             onChange={(e) => { setFirstName(e.target.value) }} />
        //         </p>
        //         <p>
        //             <label>Last Name</label><br />
        //             <input type="text" 
        //             name="lastName"
        //             value={lastName} 
        //             onChange={(e) => { setLastName(e.target.value) }} />
        //         </p>
        //         <input type="submit" />
        //     </form>
        // </div>
        <div>
            <h1 className="updateHeader">Update a Product</h1>
            <form className="formGroup" onSubmit={updateProduct}>
                <div className="form-group row">
                    <label htmlFor="inputTitle" className="col-sm-2 col-form-label">Title</label>
                    <div className="col-sm-10">
                        <input type="title" 
                        onChange = {(e)=>setTitle(e.target.value)}
                        value={title} 
                        className="form-control" id="title" placeholder="Title" />
                    </div>
                </div>
                <div className="form-group row">
                    <label htmlFor="price" className="col-sm-2 col-form-label">Price</label>
                    <div className="col-sm-10">
                        <input type="text" 
                        onChange = {(e)=>setPrice(e.target.value)} 
                        value={price}
                        className="form-control" id="price" placeholder="Price" />
                    </div>
                </div>
                <div className="form-group row">
                    <label htmlFor="desc" className="col-sm-2 col-form-label">Description</label>
                    <div className="col-sm-10">
                        <input type="text" 
                        onChange = {(e)=>setDescription(e.target.value)} 
                        value={description}
                        className="form-control" id="desc" placeholder="Description" />
                    </div>
                </div>

                    <div className="col-sm-10 update-btn">
                    <button type="submit" className="btn btn-primary">Update</button>
                    </div>
            </form>
        </div>
    )
}
