import React, { useEffect, useState } from 'react'
import axios from 'axios';
import { navigate } from '@reach/router';
import ProductForm from '../components/ProductForm'

// eslint-disable-next-line
export default props => {
    const [product, setProduct] = useState();
    const [loaded, setLoaded] = useState(false);

    // why useEffect? getting access to the existing data and displaying
    useEffect(() => {
        axios.get(`http://localhost:8000/api/products/${props.id}` )
            .then(res => {
                // setProduct(res.data);
                // check controller line 29
                setProduct(res.data);
                setLoaded(true);
            })
    }, [])

    const updateProduct = product => {
        axios.put(`http://localhost:8000/api/products/${props.id}`, product)
            .then(res => {
                console.log(res)
                // navigate to the main page after updating the form
                navigate("/")
            })
            .catch(err=>console.log(err));
    }

    return (

        <div>
            <h1 className="updateHeader">Update a Product</h1>
            {loaded && (
            <ProductForm
                onSubmitProp={updateProduct}
                initialTitle={product.title}
                initialPrice={product.price}
                initialDescription={product.description}
            />
            )}
        </div>
    )
}
