import React, { useEffect, useState } from 'react'
import axios from 'axios';
import ProductForm from '../components/ProductForm'
import ProductList from '../components/ProductList';


// eslint-disable-next-line
export default () => {
    const [ products, setProducts ] = useState([]);

    useEffect(()=>{
        axios.get("http://localhost:8000/api/products")
            // .then(res=>setProducts(res.data.products)) if res.json({products: allProducts}) instead of getting it as an array from controller
            .then(res=>setProducts(res.data))  
            .catch(err=>console.log("Error: ", err))     
    }, [])

    const removeFromDom = productId => {
        setProducts(products.filter(prod => prod._id != productId));
    }

    const addToDom = productId => {
        setProducts(products.map((value, idx) => {
            return <h3 key={idx}> {setProducts} </h3>
        }
        ))
    }

    return (
        <div className="mainForm">
            <h2>Product Manager</h2>
            <ProductForm products={products} setProducts={setProducts} />
            <ProductList products={products} removeFromDom={removeFromDom} addToDom={addToDom} />
        </div>
    )
}
