import React, { useState } from 'react'
import axios from 'axios';

// eslint-disable-next-line
export default () => {
    //keep track of what is being typed via useState hook
    const [title, setTitle] = useState(""); 
    const [price, setPrice] = useState("");
    const [description, setDescription] = useState("");
    //handler when the form is submitted
    const onSubmitHandler = e => {
        //prevent default behavior of the submit
        e.preventDefault();
        //make a post request to create a new person
        axios.post('http://localhost:8000/api/products/new', {
            title,
            price,
            description
        })
            .then(res=>console.log(res))
            .catch(err=>console.log(err))
    }
    //onChange to update firstName and lastName
    // return (
    //     <form onSubmit={onSubmitHandler}>
    //         <p>
    //             <label>Title</label><br/>
    //             <input type="text" onChange = {(e)=>setTitle(e.target.value)}/>
    //         </p>
    //         <p>
    //             <label>Price</label><br/>
    //             <input type="text" onChange = {(e)=>setPrice(e.target.value)}/>
    //         </p>
    //         <p>
    //             <label>Description</label><br/>
    //             <input type="text" onChange = {(e)=>setDescription(e.target.value)}/>
    //         </p>
    //         <input type="submit"/>
    //     </form>
    // )

    return (
        <form onSubmit={onSubmitHandler}>
            <div className="form-row align-items-center">
                <div className="form-group row">
                    <label for="inputTitle" className="col-sm-2 col-form-label">Title</label>
                    <div className="col-sm-10">
                        <input type="title" onChange = {(e)=>setTitle(e.target.value)} className="form-control" id="title" placeholder="Title" />
                    </div>
                </div>
                <div className="form-group row">
                    <label for="price" className="col-sm-2 col-form-label">Price</label>
                    <div className="col-sm-10">
                        <input type="text" className="form-control" id="price" placeholder="Price" />
                    </div>
                </div>

                <div className="form-group row">
                    <div className="col-sm-10">
                    <button type="submit" className="btn btn-primary">Sign in</button>
                    </div>
                </div>
            </div>
        </form>
    )
}
