import React, { useState, useEffect } from 'react'
import axios from 'axios';
import { navigate } from '@reach/router';

// eslint-disable-next-line
export default (props) => {
    //keep track of what is being typed via useState hook
    const [title, setTitle] = useState(""); 
    const [price, setPrice] = useState("");
    const [description, setDescription] = useState("");


    useEffect(() => {
        axios.get("http://localhost:8000/api/products")
            .then(res => {
                props.setProducts(res.data)
                navigate("/")
            })
    })


    //handler when the form is submitted
    const onSubmitHandler = e => {
        //prevent default behavior of the submit
        e.preventDefault();
        //make a post request to create a new person
        axios.post('http://localhost:8000/api/products/new', {
            title,
            price,
            description
        })
            .then(res=> console.log(res, "** New product created"))
            .catch(err=>console.log(err))
        // doesn't clear the input field????
        setTitle("");
        setPrice("");
        setDescription("");
    }

    return (
        <form className="formGroup" onSubmit={onSubmitHandler}>
            {/* <div className="form-group align-items-center"> */}
                <div className="form-group row">
                    <label htmlFor="inputTitle" className="col-sm-2 col-form-label">Title</label>
                    <div className="col-sm-10">
                    {/* <div className="col-sm-6"> */}
                        <input type="title" onChange = {(e)=>setTitle(e.target.value)} className="form-control" id="title" placeholder="Title" />
                    </div>
                </div>
                <div className="form-group row">
                    <label htmlFor="price" className="col-sm-2 col-form-label">Price</label>
                    <div className="col-sm-10">
                        <input type="text" onChange = {(e)=>setPrice(e.target.value)} className="form-control" id="price" placeholder="Price" />
                    </div>
                </div>
                <div className="form-group row">
                    <label htmlFor="desc" className="col-sm-2 col-form-label">Description</label>
                    <div className="col-sm-10">
                        <input type="text" onChange = {(e)=>setDescription(e.target.value)} className="form-control" id="desc" placeholder="Description" />
                    </div>
                </div>

                {/* <div className="form-group row"> */}
                    <div className="col-sm-10">
                    <button type="submit" className="btn btn-primary">Create</button>
                    </div>
                {/* </div> */}
            {/* </div> */}
        </form>
    )
}


