import React from 'react';
import axios from 'axios';
import { Link } from '@reach/router';

// eslint-disable-next-line
export default (props) => {
    const { removeFromDom } = props;
    const deleteProduct = (productId) => {
        axios.delete('http://localhost:8000/api/products/' + productId)
            .then(res => {
                removeFromDom(productId)
            })
    }

    return (
        <div className="displayAll">
            {/* make sure products are in ARRAY or check the data type and device how to iterate */}

            <h1>All Products:</h1>
            {props.products.map((prod, idx)=>{
                return <h4 className="productTitle" key={idx}> 
                    {/* <Link to={`/products/:${product.id}`}> {product.title} </Link> */}
                    {/* <Link to="/products/:id"> {product.title} </Link> */}
                    <Link to={`/${prod._id}`}> {prod.title} </Link>

                    <button className="btn btn-secondary" onClick={(e)=>{deleteProduct(prod._id)}}>
                        Delete
                    </button>
                    </h4>
            })}

        </div>
    )
}


