import React, { useEffect, useState } from 'react'
import axios from 'axios';
import { navigate } from '@reach/router';

// eslint-disable-next-line
export default props => {
    const [product, setProduct] = useState();
    const [loaded, setLoaded] = useState(false);


    // getting access to the existing data and displaying
    useEffect(() => {
        axios.get(`http://localhost:8000/api/products/${props.id}` )
            .then(res => {
                setProduct(res.data);
                setLoaded(true);
            })
    }, [])

    const updateProduct = e => {
        e.preventDefault();
        axios.put(`http://localhost:8000/api/products/${props.id}`, {
            title,
            price,
            description
        })
            .then(res => {
                console.log(res)
                // navigate to the main page after updating the form
                navigate("/")
            })
            .catch(err=>console.log(err));
    }
        // const updateProduct = e => {
        //     e.preventDefault();
        // }
    return (

        <div>
            <h1 className="updateHeader">Update a Product</h1>
            <form className="formGroup" onSubmit={updateProduct}>
                <div className="form-group row">
                    <label htmlFor="inputTitle" className="col-sm-2 col-form-label">Title</label>
                    <div className="col-sm-10">
                        <input type="title" 
                        onChange = {(e)=>setTitle(e.target.value)}
                        value={title} 
                        className="form-control" id="title" />
                    </div>
                </div>
                <div className="form-group row">
                    <label htmlFor="price" className="col-sm-2 col-form-label">Price</label>
                    <div className="col-sm-10">
                        <input type="text" 
                        onChange = {(e)=>setPrice(e.target.value)} 
                        value={price}
                        className="form-control" id="price" />
                    </div>
                </div>
                <div className="form-group row">
                    <label htmlFor="desc" className="col-sm-2 col-form-label">Description</label>
                    <div className="col-sm-10">
                        <input type="text" 
                        onChange = {(e)=>setDescription(e.target.value)} 
                        value={description}
                        className="form-control" id="desc" />
                    </div>
                </div>

                    <div className="col-sm-10 update-btn">
                    <button type="submit" className="btn btn-primary">Update</button>
                    </div>
            </form>
        </div>
    )
}
