import React, { useEffect, useState } from 'react'
import axios from 'axios';
import { Link } from '@reach/router';

// eslint-disable-next-line
export default props => {
    const [product, setProduct] = useState({});

    useEffect(() => {
        // axios.get("http://localhost:8000/api/people/" + props.id)
        axios.get(`http://localhost:8000/api/products/${props.id}`)
            .then(res => setProduct(res.data))
            .catch(err => console.log(err));
    }, [])

    const removeFromDom = productId => {
        setProduct(product.filter(prod => prod._id != productId));
    }

    const deleteProduct = (productId) => {
        axios.delete(`http://localhost:8000/api/products/${productId}`)
            // .then(res => removeFromDom(res.data._id)) => same as below
            .then(res => removeFromDom(productId))
            .catch(err=>console.log(err));
    }

    return (
        <div className="detailView">
            <h1 style={{"marginBottom":"20px"}}>{product.title}</h1>
            <h4>Price: ${product.price}</h4>
            <h4>Description: {product.description}</h4>
            <div>
                <h5 style={{"margin":"30px 0"}}><Link to={"/" + product._id + "/edit"}>EDIT</Link></h5>
                <button className="btn btn-secondary" onClick={(e)=>{deleteProduct(product._id)}}>
                Delete
                </button>
            </div>

            <h5><Link to='/'> Back to Main Page </Link></h5>
        </div>
    )
}
