import React, { useEffect, useState } from 'react'
import axios from 'axios';
import ProductForm from '../components/ProductForm'
import ProductList from '../components/ProductList';


// eslint-disable-next-line
export default () => {
    const [ products, setProducts ] = useState([]);
    const [loaded, setLoaded] = useState(false);

    useEffect(()=>{
        axios.get("http://localhost:8000/api/products")
            // .then(res=>setProducts(res.data.products)) if res.json({products: allProducts}) instead of getting it as an array from controller
            .then(res=>{
                setProducts(res.data)
                setLoaded(true);
            })  
            .catch(err=>console.log("Error: ", err))     
    }, [])

    const removeFromDom = productId => {
        setProducts(products.filter(prod => prod._id != productId));
    }

    const createProduct = products => {
        axios.post('http://localhost:8000/api/products/new', products)
            .then(res=>{
                setProducts([...products, res.data.products]);
            })
    }


    return (
        <div className="mainForm">
            <h2>Product Manager</h2>
            <ProductForm onSubmitProp={createProduct} initialTitle="" initialPrice="" initialDescription="" />
            {loaded && <ProductList products={products} removeFromDom={removeFromDom}  /> }
        </div>
    )
}
